#Insertion Sort Method 1 (New array)

def add(array, item):
  for i in range(len(array)):
    if array[i] > item:
      array.insert(i, item)
      return
  array.append(item)

def insertion_sort_method1(array):
  sorted_array = []
  while len(array) > 0:
    add(sorted_array, array.pop(0))
    #print(sorted_array) # show the intermediate outputs ????
  return sorted_array
# Quick test
print(insertion_sort_method1([1, 5, 4, 2, 8]))

#Recursive Merge Sort
def merge(l1, l2): #helped function for merge_sort, merges 2 sorted lists
  l3 = []
  while len(l1) > 0 or len(l2) > 0:
    if len(l1) == 0:
      l3.extend(l2)
      return l3
    elif len(l2) == 0:
      l3.extend(l1)
      return l3
    else:
      l3.append(l1.pop(0) if l1[0] < l2[0] else l2.pop(0))
  return l3


def merge_sort(array): #splits the lists and merges with recursion
  length = len(array)
  if length <= 1:
    return array
  left = array[0:length//2]
  right = array[length//2:]
  return merge(merge_sort(left), merge_sort(right))
